import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css"
import { useEffect, useState } from "react";
import './App.css';

function App() {
  const apiKey = "f56f24967aaf51182d1d4df628297c6d"
  var [fea,setFea]=useState();
  useEffect( ()=>{
      loadapi();
  },[])
  var loadapi=async()=>{
      var res=await axios.get('https://a2zithub.org/nweb/api/future_api');
      setFea(res.data);
    }
    console.log(fea);


  const [inputCity, setInputCity] = useState("")
  const [data, setData] = useState({})


  const getWetherDetails = (cityName) => {
    if (!cityName) return
    const apiURL = "https://api.openweathermap.org/data/2.5/weather?q=" + cityName + "&appid=" + apiKey
    axios.get(apiURL).then((res) => {
      console.log("response", res.data)
      setData(res.data)
    }).catch((err) => {
      console.log("err", err)
    })
  }

  const handleChangeInput = (e) => {
    console.log("value", e.target.value)
    setInputCity(e.target.value)
  }

  const handleSearch = () => {
    getWetherDetails(inputCity)
  }


  return (
    // ///////////////////////////////////////////////////////////////////////////////
    <>

      <div className="container">
        <div className="card">
          <div className="row">
            <div className="col-12 left">
              <div className="row top">
              <div className="d-grid gap-3 col-4 mt-4">
            <input type="text" className="form-control"
              value={inputCity}
              onChange={handleChangeInput} />
            <button className="btn btn-primary" type="button"
              onClick={handleSearch}
            >Search</button>
          </div>
                <div className="col">Date</div>
                <div className="col">Rain map</div>
              </div>
              <div className="row">
                <div className="col-7 temp">

                  {
                    data?.map((item)=>{
                      return(
                          <>
                            <div className=" wetherResultBox">
                              <h5 className="weathorCity">
                                {data?.name}
                              </h5>
                              <h6 className="weathorTemp">{((data?.main?.temp) - 273.15).toFixed(2)}°C</h6>
                            </div>
                          </>
                      )
                  })
                  }
                </div>
                <div className="col-5 time"><p>11:00</p><h2><b>Saturday</b></h2><p>Cloudy</p></div>
              </div>
              <div className="row bottom">
                <div className="col"><hr/></div>
                <div className="col border">
                  <div className="row">Sab</div>
                  <div className="row"><b>-4&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Sun</div>
                  <div className="row"><b>-4&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Mon</div>
                  <div className="row"><b>-5&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Tue</div>
                  <div className="row"><b>-10&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Wed</div>
                  <div className="row"><b>-4&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Thu</div>
                  <div className="row"><b>-2&deg;</b></div>
                </div>
                <div className="col">
                  <div className="row">Fri</div>
                  <div className="row"><b>0&deg;</b></div>
                </div>
                <div className="col"><hr /></div>
              </div>
            </div>

          </div>
        </div>
      </div>
     </>
  );
}

export default App;
